import React, { Component } from 'react'

export class Counter extends Component {
    constructor(props) {
        super(props)
    
        this.state = {
             Count:0
        }
    }
    IncrementCounter=()=>{
        this.setState(prv=>{
            Count:prv+1
        })
    }
    render() {
    const {cvalue}=this.state.Count
        return (
            <div>
                <button onClick={this.IncrementCounter}>clicker {cvalue}</button>
            </div>
        )
    }
}

export default Counter
