import React, { Component } from 'react'
import Counter from './Counter'


export class Counters extends Component {
    state={
        counters :[
            {id:1,value:4},
            {id:2,value:0},
            {id:3,value:0},
            {id:4,value:0}
        ]
    };
    dercrementCounter=counter=>{
        const counters=[...this.state.counters];
        const index=counters.indexOf(counter);
        counters[index]={...counter};
        counters[index].value--;
        this.setState({
            counter:this.state.count-1
        })
    }
    incrementCounter=()=>{
        this.setState({
            count:this.state.count+1
        })
    }
    deleteItem=counterId=>{
        const counters=this.state.counters.filter(c=>c.id!==counterId)
        this.setState({
            counters:counters
        })
    }
    resetCounter=()=>{
        const counters=this.state.map(c=>{
            c.value=0;
            return c;
        });
        
        this.setState({
            counters:counters
        }) 
    }
    render() {
        return (
            <div>
                 <button className="btn btn-primary  pl-3 pr-3 pt-0 pb-0 m-5" onClick={this.resetCounter}>Reset</button>
                {this.state.counters.map(count=> <Counter count={count}  deleteItem={this.deleteItem} key={count.id}/>)}
            </div>
        )
    }
}

export default Counters
