import React, { Component } from 'react'

class EventBind extends Component {
    constructor(props) {
        super(props)
    
        this.state = {
             message:"Hello friends"
        }
     //   this.clickme=this.clickme.bind(this)
    }
    // clickme(){
    //     this.setState({
    //         message:"welcome friends"
    //     })
    // }
    clickme=()=>{
        this.setState({
            message:"welcome friends"
        })
    }
    render() {
        return (
            <div>
                <h1>{this.state.message}</h1>
               {/*<button onClick={this.clickme.bind(this)}>clickon me Event Bind</button>*/}
                {/* <button onClick={()=>this.clickme()}>clickon me Event Bind</button>  */}
                <button onClick={this.clickme}>clickon me Event Bind</button> 
            </div>
        )
    }
}

export default EventBind
