import React, { Component } from 'react'

export class Counter extends Component {
    constructor(props) {
        super(props)
    
        this.state = {
             count:0
        }

    }
    IncrementCounter=()=>{
        this.setState(prv=>{
            return{count:prv+1}
        })
    }
    render() {
    
        return (
            <div>
                <button onClick={this.IncrementCounter}>clicker {this.state.Count}</button>
            </div>
        )
    }
}

export default Counter
