import React, { Component } from 'react'

export class hoverjs extends Component {
    constructor(props) {
        super(props)
    
        this.state = {
             count:0
        }
        
    }
    incrementCounter=()=>{
        this.setState(prv=>{
            count=prv.state.count;
        })
    }
    render() {
        const {count}=this.state
        return (
            <div>
                <h1 onMouseOver={this.incrementCounter}>Hii am Dipen  {count} </h1>
            </div>
        )
    }
}

export default hoverjs
