import React, { Component } from 'react'

export class FormComp extends Component {
    constructor(props) {
        super(props)
    
        this.state = {
             name:'dipen',
             message:''
        }
    }
    changeUsername = event=>{
    this.setState({
            name:event.target.value
        })
    }
    render() {
        return (
           <form ons>
                <div>

                <input type="text" value={this.state.name} onChange={this.changeUsername}></input>

            </div>
            <div>

                <button type="submit"></button>
            </div>
           </form>
        )
    }
}

export default FormComp
