import React, { Component } from 'react'

class RefsDemo extends Component {
    constructor(props) {
        super(props)
    
      this.inpR=React.createRef()
      this.inpRef=null;
      this.inpRviaOther= ele =>{
          this.inpRef=ele;
      }
    }
    
    componentDidMount(){
        if(this.inpRef){
            this.inpRef.focus
        }
       this.inpR.current.focus()
    }
    clickHandeler=()=>{
        alert(this.inpR.current.value)
    }
    render() {
        return (
            <div>
                <input type='text' ref={this.inpR}></input>
                <input type='text' ref={this.inpRviaOther}></input>
                <input type='button' onClick={this.clickHandeler} ></input>
            </div>
        )
    }
}

export default RefsDemo
