import React, { Component } from 'react'

class RefsDemo extends Component {
    constructor(props) {
        super(props)
    
      this.inpR=React.createRef()
      this.inpRef
      this.inpRviaOther= ele =>{
          this.inpRef=ele;
      }
    }
   
    componentDidMount(){
       this.inpR.current.focus()
    }
    render() {
        return (
            <div>
                <input type='text' ref={this.inpR}></input>
                <input type='button'  ref={this.inpRviaOther}></input>
            </div>
        )
    }
}

export default RefsDemo
