import React, { Component } from 'react'
import ChildComp from './ChildComp'
class ParentComp extends Component {
    constructor(props) {
        super(props)
    
        this.state = {
             message:"Hellow Dipen"
        }
        this.showalert=this.showalert.bind(this)
    }
    showalert(Chilename){
        alert(`welcome ${this.state.message} ${Chilename}`)
    }
    render() {
        return (
            <div>
                <ChildComp rf={this.showalert}></ChildComp>
            </div>
        )
    }
}

export default ParentComp
