import React, { Component } from 'react'
import Counter from './Counter'


export class Counters extends Component {
   
    render() {
        const {dercrementCounter,incrementCounter,deleteItem,count,resetCounter}=this.props
        return (
            <div>
                 <button className="btn btn-primary  pl-3 pr-3 pt-0 pb-0 m-5" onClick={this.resetCounter}>Reset</button>
                {this.state.counters.map(count=>
                    <Counter count={count} 
                             deleteItem={this.deleteItem} 
                             incrementCounter={this.incrementCounter}
                             dercrementCounter={this.dercrementCounter}
                             key={count.id}/>)}
            </div>
        )
    }
}

export default Counters
