import React, { Component } from 'react'

export class Input extends Component {
    constructor(props) {
        super(props)
    
        this.refToParent=reac;
    }
    
    ChangeParentFocus=()=>{
        this.refToParent.current.focus()
    }
    render() {
        return (
            <div>
               <input type="text" ref={this.refToParent}></input>
            </div>
        )
    }
}

export default Input
