import React, { Component } from 'react'

 class ConditionalRendering extends Component {
     constructor(props) {
         super(props)
     
         this.state = {
              log:true
         }
     }
     
    render() {
        if(!(this.state.log)){
            return (
                <div>
                   please login 
                </div>
            )

        }else{
            return (
                <div>
                    welcome
                </div>
            )
        }
        
    }
}

export default ConditionalRendering
